    let mut steps_to_basement: i128 = 0;
    let mut found_basement: bool = false;
    let route: Chars<'_> = floorplan.chars();

    route.for_each(|step| {
        if floor == -1 {
            found_basement = true;
        }

        if found_basement == false {
            steps_to_basement += 1;
        }

        if step == '(' {
            floor += 1;
        } else {
            floor -= 1;
        }
    });

